import React from 'react';
import './App.css';
import './Card'
import './Card-list'

const Task = (value) => {
  return (
    <li key={value}>{value}</li>
  );
}

const tarefas = ['Acordar', 'Tomar café', 'Escovar os dentes'];

class App extends React.Component {
  render() {
    return (
      <div>
        <ul>{ tarefas.map(tarefa => Task(tarefa)) }</ul>             
      </div>      
    );
  }  
}

export default App;
