    this.btn1 = this.btn1.bind(this);  // para fazer a conexao com o this dentro do render, senão ele não enxerga o this
    // console.log('Constructor teste')
  }

  btn1() {
    this.setState({
      numeroDeCliques: 1
    })
    // console.log('Clicou no botão 1');
  }

  btn2() {
    // console.log('Clicou no botão 2');
  }

  btn3() {
    // console.log('Clicou no botão 3');
  }

  render() {
    // console.log(this);
    return (
    <div>
      <button onClick={this.btn1}>{this.state.numeroDeCliques}</button>
      <button onClick={this.btn2}>Botão 2</button>
      <button onClick={this.btn3}>Botão 3</button>
    </div>
    )
  }    
}

export default App;
